import React from "react";
import { useState } from "react";
import { Configuration, OpenAIApi } from "openai";

const Bot = () => {

    const configuration = new Configuration({
        apiKey: "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",
    });
    const openai = new OpenAIApi(configuration);

    const [prompt, setPrompt] = useState("");
    const [result, setResult] = useState("");
    const [loading, setLoading] = useState(false);
    const [messages, setMessages] = useState([]);

    const handleClick = async () => {
        setLoading(true);

        // Add the user's message to the conversation history
        await new Promise((resolve) => {
          setMessages((prevMessages) => [...prevMessages, { text: prompt, sender: "user" }]);
          resolve();
        });

        try {
          // Concatenate conversation history to be included in the prompt
          const history = messages
            .map((msg) => `${msg.sender === "user" ? "User:" : "AI:"} ${msg.text}`)
            .join("\n");
          const fullPrompt = history + "\nUser: " + prompt + "\nAI:";

          const response = await openai.createCompletion({
            model: "text-davinci-003",
            prompt: fullPrompt,
            temperature: 0.5,
            max_tokens: 100,
          });

          // Add the AI's response to the conversation history
          const responseText = response.data.choices[0].text.trim();
          setMessages((prevMessages) => [...prevMessages, { text: responseText, sender: "ai" }]);
          setResult(responseText);
        } catch (error) {
          console.error(error);
        }

        setLoading(false);
        setPrompt(""); // Clear the input field
      };


    return (
        <div className="container">
            <h1>Real Estate App</h1>
            <h2>Your AI Real Estate Assistant</h2>
            <div className="chatbox">
                <div className="messages">
                    {messages.map((message, index) => (
                        <div key={index} className={`message ${message.sender}`}>
                            <span className="sender">{message.sender}:</span> {message.text}
                        </div>
                    ))}
                </div>
                <div className="input-area">
                    <input
                        type="text"
                        id="user-input"
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        placeholder="Type your message here..."
                    />
                    <button id="send-button" onClick={handleClick} disabled={loading || prompt.length === 0}>
                        {loading ? "Sending..." : "Send"}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Bot;
