import { useState } from "react";
import { Configuration, OpenAIApi } from "openai";
import "./App.css";

function App() {
  const configuration = new Configuration({
    apiKey: "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",
  });
  const openai = new OpenAIApi(configuration);

  const [prompt, setPrompt] = useState("");
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]); // Add this line

  const handleClick = async () => {
    setLoading(true);

    // Add the user's message to the conversation history
    setMessages([...messages, { text: prompt, sender: "user" }]);

    try {
      // Concatenate conversation history to be included in the prompt
      const history = messages.map(msg => `${msg.sender === "user" ? "User:" : "AI:"} ${msg.text}`).join("\n");
      const fullPrompt = history + "\nUser: " + prompt + "\nAI:";

      const response = await openai.createCompletion({
        model: "text-davinci-003",
        prompt: fullPrompt,
        temperature: 0.5,
        max_tokens: 100,
      });

      // Add the AI's response to the conversation history
      const responseText = response.data.choices[0].text.trim();
      setMessages([...messages, { text: responseText, sender: "ai" }]);
      setResult(responseText);
    } catch (error) {
      console.error(error);
    }

    setLoading(false);
  };

  return (
    <main className="main d-flex align-items-center justify-content-center">



      <div className="container-lg">
        <h1 className="title text-center mb-4">Real Estate AIO</h1>

        //This is where the messages will be displayed Above the propmt
        <div className="message-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender}`}>
              <span className="sender">{message.sender}:</span> {message.text}
            </div>
          ))}
        </div>
        <div className="mb-3">
          <textarea
            type="text"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="Write your prompt.."
            className="form-control textarea"
          ></textarea>
        </div>

        <button
          onClick={handleClick}
          disabled={loading || prompt.length === 0}
          className="btn btn-primary mb-3"
        >
          {loading ? "Generating..." : "Generate"}
        </button>

        <pre className="result">{result}</pre>
      </div>
    </main>

  );
}

export default App;
